    fun requestInfo(callback: Consumer<List<HomeListInfo>>) {
        val subscribe = Observable.create<HomeResponse> {
            //模拟请求
            it.onNext(Gson().fromJson(json, HomeResponse::class.java))
            it.onComplete()
        }.map {
            val list = ArrayList<HomeListInfo>()
            val heard = HomeListInfo()
            heard.minorIconList = it.data.minorIconList
            heard.heardBanner=it.data.banner
            heard.currType = HomeListInfo.HEARD_TYPE
            list.add(heard)


            it.data.squareVideos?.forEach {
                list.add(createTileType(it.name))
                val contentInfo = HomeListInfo()
                contentInfo.currType = HomeListInfo.LIST_SQUARE_TYPE
                contentInfo.squareVideos = it.videoTemplates
                list.add(contentInfo)
            }

            it.data.gridVideos.forEach {
                //构建标题
                list.add(createTileType(it.name))
                for (index in it.videoTemplates!!.indices) {
                    val videoTemplatesBean = it.videoTemplates[index]
                    //构建内容模块
                    val contentInfo = HomeListInfo()
                    contentInfo.currType = HomeListInfo.CONTENT_STYLE_2
                    contentInfo.listIndex = index
                    contentInfo.videoTemplatesBean = videoTemplatesBean
                    list.add(contentInfo)
                }
            }


            val bannerInfo = HomeListInfo()
            bannerInfo.middleBanner = it.data.banner_middle
            bannerInfo.currType = HomeListInfo.BANNER_TYPE
            list.add(bannerInfo)

            it.data.rectangleVideos?.forEach {
                list.add(createTileType(it.name))
                val contentInfo = HomeListInfo()
                contentInfo.currType = HomeListInfo.LIST_RECTANGLE_TYPE
                contentInfo.rectangleVideos = it.videoTemplates
                list.add(contentInfo)
            }


            list.add(createTileType("最近热门"))

            for (index in it.data.videoTemplates.indices) {
                val videoTemplatesBean = it.data.videoTemplates[index]
                //构建内容模块
                val listContentInfo = HomeListInfo()
                listContentInfo.currType = HomeListInfo.CONTENT_STYLE_1
                listContentInfo.videoTemplatesBean = videoTemplatesBean
                listContentInfo.listIndex = index
                list.add(listContentInfo)
            }

            return@map list
        }.subscribeOn(Schedulers.io()).observeOn(AndroidSchedulers.mainThread()).subscribe(callback)
    }


    private fun createTileType(title: String): HomeListInfo {
        //构建标题
        val titleInfo = HomeListInfo()
        titleInfo.currType = HomeListInfo.TITLE_TYPE
        titleInfo.title = title
        return titleInfo
    }


